sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var gr = new GlideRecord('x_853492_lms_leave_calculator');
	gs.addInfoMessage("Location : "+current.location.country);
	gr.addQuery('country.name',current.location.country);
	gr.query();
	if(gr.next())
		{
			if(gr.leave_assignment=='yr')
				{
			gs.addInfoMessage("Type : "+gr.leave_assignment);
					var gdt = new GlideDateTime();
					                             //-1 is used to include leave days for current month also
					var currentMth = gdt.getMonthLocalTime() - 1;   
					var monthsLeft = 12 - currentMth;    //months left to the end of year
					var leavesPerMonth = gr.leaves/12;
					var totalLeaves = leavesPerMonth * monthsLeft;

					var leaveBucket = new GlideRecord('x_853492_lms_leave_bucket');
					leaveBucket.initialize();
					leaveBucket.leave_type = gr.leave_type;
					leaveBucket.accrued = totalLeaves;  //how many total leaves gained/accumulated
					leaveBucket.balance = totalLeaves;
					leaveBucket.taken = 0;
					leaveBucket.employee = current.sys_id;
					leaveBucket.insert();

				}
			else if(gr.leave_assignment=='mth')
				{
					var perMonthM = gr.leaves/12;

					var leaveBucketM = new GlideRecord('x_853492_lms_leave_bucket');
					leaveBucketM.initialize();
					leaveBucketM.leave_type = gr.leave_type;
					leaveBucketM.accrued = perMonthM;  //how many total leaves gained/accumulated
					leaveBucketM.balance = perMonthM;
					leaveBucketM.taken = 0;
					leaveBucketM.employee = current.sys_id;
					leaveBucketM.insert();

					//Here, we need to have additional logic to increase the leave every month

				}
		}


})(current, previous);
